import React, { useState } from "react";

function Button({ formContents, setFormContents }) {
    const submit = () => {
    const {date, description, category, amount} = formContents;
    const updatedForm = {
        date: date,
        description: description,
        category: category,
        amount: amount
    }
    setFormContents([...formContents, updatedForm]);
    };
    return (
        <button className="button" onSubmit={submit}>Add Transaction.</button>
    )
}


export default Button;
