import React, { useState } from "react";
import Button from "./Transaction";

function Form() {
   const initialFormContent = {
      date: "",
      description: "",
      category: "",
      amount: ""
   };
   const [updatedFormContents, setFormContents] = useState([initialFormContent]);

   const handleInputChange = (event, index) => {
      const { name, value } = event.target;
      setFormContents(prevState => {
         const updatedContents = [...prevState];
         updatedContents[index] = {
            ...updatedContents[index],
            [name]: value
         };
         // Add a new form if the last form's fields are edited
         if (index === updatedContents.length - 1 && Object.values(updatedContents[index]).every(val => val !== "")) {
            updatedContents.push(initialFormContent);
         }
         return updatedContents;
      });
   }

   return (
      <>
         {updatedFormContents.map((formContent, index) => (
            <div key={index}>
               <form className="form">
                  <strong>Date:</strong>
                  <input
                     type="date"
                     name="date"
                     placeholder="mm/dd/yy"
                     value={formContent.date}
                     onChange={(event) => handleInputChange(event, index)}
                  />
                  <input
                     type="text"
                     name="description"
                     placeholder="Description"
                     value={formContent.description}
                     onChange={(event) => handleInputChange(event, index)}
                  />
                  <input
                     type="text"
                     name="category"
                     placeholder="Category"
                     value={formContent.category}
                     onChange={(event) => handleInputChange(event, index)}
                  />
                  <input
                     type="text"
                     name="amount"
                     placeholder="Amount"
                     value={formContent.amount}
                     onChange={(event) => handleInputChange(event, index)}
                  />
               </form>
            </div>
         ))}
         <Button updatedFormContents={updatedFormContents}/>
      </>
   )
}

export default Form;
