import React, { useState } from "react";
import Button from "./Transaction";

function Form() {
   const formContents = {
      date: "",
      description: "",
      category: "",
      amount: ""
   }
   const [updatedFormContents, setFormContents] = useState(formContents);

   const handleInputChange = (event)=>{
      const {name, value} = event.target;
      setFormContents({...updatedFormContents, [name]: value});
   }
   return (
      <>
         <form className="form">
            <strong>Date:</strong><input type="date" name="date" placeholder="mm/dd/yy" value={updatedFormContents.date} onChange={handleInputChange}/>
            <input type="text" name="description" placeholder="Description" value={updatedFormContents.description} onChange={handleInputChange}/>
            <input type="text" name="category" placeholder="Category" value= {updatedFormContents.category} onChange={handleInputChange} />
            <input type="text" name="amount" placeholder="Amount" value={updatedFormContents.amount} onChange={handleInputChange}/>
         </form>
         <Button updatedFormContents={updatedFormContents}/>
      </>
   )
}

export default Form;
