import React, { useState } from "react";
import Button from "./Transaction";

function Form() {
   const initialFormContent = {
      date: "",
      description: "",
      category: "",
      amount: ""
   };

   // State to manage current form contents
   const [formContents, setFormContents] = useState(initialFormContent);

   // State to manage past transactions
   const [transactions, setTransactions] = useState([]);

   const handleInputChange = (event) => {
      const { name, value } = event.target;
      setFormContents(prevState => ({
         ...prevState,
         [name]: value
      }));
   }

   const addTransaction = () => {
      // Add current form contents to transactions array
      setTransactions(prevTransactions => [...prevTransactions, formContents]);
      // Reset form contents
      setFormContents(initialFormContent);
   }

   return (
      <>
         <form className="form">
            <strong>Date:</strong>
            <input
               type="date"
               name="date"
               placeholder="mm/dd/yy"
               value={formContents.date}
               onChange={handleInputChange}
            />
            <input
               type="text"
               name="description"
               placeholder="Description"
               value={formContents.description}
               onChange={handleInputChange}
            />
            <input
               type="text"
               name="category"
               placeholder="Category"
               value={formContents.category}
               onChange={handleInputChange}
            />
            <input
               type="text"
               name="amount"
               placeholder="Amount"
               value={formContents.amount}
               onChange={handleInputChange}
            />
         </form>
         <Button onClick={addTransaction} />

         {/* Display past transactions */}
         <table>
            <thead>
               <tr>
                  <th>Date</th>
                  <th>Description</th>
                  <th>Category</th>
                  <th>Amount</th>
               </tr>
            </thead>
            <tbody>
               {transactions.map((transaction, index) => (
                  <tr key={index}>
                     <td>{transaction.date}</td>
                     <td>{transaction.description}</td>
                     <td>{transaction.category}</td>
                     <td>{transaction.amount}</td>
                  </tr>
               ))}
            </tbody>
         </table>
      </>
   )
}

export default Form;
