import React, { useState } from "react";

function Button({ updatedFormContents }) {
    const [transactions, setTransactions] = useState(updatedFormContents);

    const submit = () => {
        // Add a new transaction to the transactions array
        const newTransaction = {
            date: "",
            description: "",
            category: "",
            amount: ""
        };
        setTransactions(prevTransactions => [...prevTransactions, newTransaction]);
    };

    return (
        <button className="button" onClick={submit}>Add Transaction</button>
    );
}

export default Button;
