import React,{useState} from "react";
import "./App.css"
import Form from "./components/Form.js";
import SearchBar from "./components/SearchBar.js";
import Table from "./components/Table.js";

function App(){
            const [payments, setPayments] = useState([]);
            const [searchWord, setSearchWord] = useState("");
            const addPayments = (formContent) => {
              setPayments([...payments, formContent]);
            }
            const filterSearchWords = payments.filter((payment) =>{
              payment.description.toLowerCase().includes(searchWord.toLowerCase())
              });

            return (
              <div className = "App">
                <header>
                  <h1>The Royal Bank Of Flatiron</h1>
                </header>
                <SearchBar onChange={setSearchWord} />
                <Form onSubmit={addPayments} />
                <Table transactions = {filterSearchWords} />
              </div>
            );
            }       

export default App;
