import React from "react";

function Button(props) {
    function submit() {
        // Get the updated form contents from props
        const components = props.updatedFormContents;

        // Call the push method on components to add a new transaction
        const array = [...components, {
            date: "",
            description: "",
            category: "",
            amount: ""
        }];

        // Log the updated array
        console.log(array);
    }

    return (
        <button className="button" onClick={submit}>Add Transaction</button>
    );
}

export default Button;
